  line-height: 1.6;
  color: #bdc3c7;
}

.footer-section ul {
  list-style: none;
  padding: 0;
}

.footer-section ul li {
  margin-bottom: 10px;
}

.footer-section ul li a {
  color: #ecf0f1;
  text-decoration: none;
  transition: color 0.3s;
}

.footer-section ul li a:hover {
  color: #3498db;
}

.social-links {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.social-links a {
  display: flex;
  align-items: center;
  gap: 10px;
  color: #ecf0f1;
  text-decoration: none;
  transition: color 0.3s;
}

.social-links a:hover {
  color: #3498db;
}

.social-links svg {
  width: 24px;
  height: 24px;
}

.footer-bottom {
  max-width: 1200px;
  margin: 0 auto;
  padding-top: 20px;
  border-top: 1px solid #34495e;
  text-align: center;
  color: #95a5a6;
  font-size: 0.9em;
}
</style>
